import gaia7 from "../images/gaia7.jpg";
import gaia8 from "../images/gaia8.jpg";

function GaiaEvent() {
  const data = [
    {
      image: gaia1,
      // caption: "San Francisco"
    },
    {
      image: gaia2,
      // caption: "Scotland"
    },
    {
      image: gaia3,
      // caption: "Darjeeling"
    },
    {
      image: gaia4,
      // caption: "Darjeeling"
    },
    {
      image: gaia5,
      // caption: "San Francisco"
    },
    {
      image: gaia6,
      // caption: "Darjeeling"
    },
    {
      image: gaia7,
      // caption: "Darjeeling"
    },
    {
      image: gaia8,
      // caption: "Scotland"
    },
  ];

  const captionStyle = {
    fontSize: "3vh",
    fontWeight: "bold",
  };
  const slideNumberStyle = {
    fontSize: "20px",
    fontWeight: "bold",
    color: "black",
  };

  return (
    <div className="gaia-section">
      <div className="event-content">
        <div className="back-arrow">
          <Link to="/">
            <HiArrowNarrowLeft />{" "}
          </Link>
        </div>
        <div className="event-titel">
          <h1 className="pageTitle">GAIA EVENT 2021</h1>
          <p className="descriptionText">
            En mycket trevlig och inspirerande kv√§ll tillsammans med Gaia
          </p>
        </div>
        {/*------------ bild gallery -------------*/}
        <div className="event-bild">
          <div style={{ textAlign: "center" }}>
            <div style={{ padding: "2%" }}>
              <Carousel
                data={data}
                time={2000}
                width="140vh"
                height="80vh"
                captionStyle={captionStyle}
                radius="1%"
                slideNumber={true}
                slideNumberStyle={slideNumberStyle}
                captionPosition="bottom"
                automatic={true}
                dots={true}
                pauseIconColor="white"
                pauseIconSize="10vh"
                slideBackgroundColor="lightgray"
                slideImageFit="contain"
                thumbnails={true}
                // thumbnailWidth="6%"
                thumbnailWidth="5%"
                style={{
                  textAlign: "center",
                  maxWidth: "140vh",
                  maxHeight: "80vh",
                  margin: "4% auto",
                }}
              />
            </div>
          </div>
        </div>{" "}
        {/*------------ bild gallery -------------*/}
        <div className="event-text">
          <h1 className="pageTitle">Inspirationskv√§ll med Gaia </h1>

          <p className="descriptionText">
            Mettes huvudsponsor Gaia brukar h√•lla i minst ett f√∂retagsevent per
            √•r. I √•r var detta ett fysiskt bes√∂k p√• Gaias kontor i Norrk√∂ping
            d√§r alla tjejer och icke-bin√§ra var v√§lkomna, d√§r de bj√∂ds p√• mat
            och bubbel. Under kv√§llen svarade Gaia p√• fr√•gor och gav en inblick
            √∂ver hur arbetslivet d√§r kunde se ut. Studenterna tyckte att det var
            v√§ldigt uppskattat och blev mycket inspirerade under Gaias event.
          </p>
          <p className="descriptionText">
            F√∂rra √•ret var f√∂retagseventet med Gaia en digital kv√§llsevent med
            temat UX.
          </p>

          <div className="descriptionText" style={{ marginTop: 2 }}>
            Vill du veta mer om Gaia eller har du n√•gra funderingar om dina
            m√∂jligheter efter examen s√• kan du kolla in
            <a href="gaia.se"> Gaias hemsida </a>!
          </div>
        </div>
      </div>
    </div>
  );
}

export default GaiaEvent;
